import { useContext } from "react";
import { MdClose } from "react-icons/md";
import prod from "../../../assets/products/watch-prod-1.webp";
import { Context } from "../../../utils/context";


import "./CartItem.scss";
const CartItem = () => {
    const { cartItems, handleCartProductQuantity , handleRemoveFromCart } = useContext(Context);
    return (
        <div className="cart-products">
            {cartItems?.map((item) => (
                <div key={item.id} className="cart-product">
                    <div className="img-container">
                        <img src={process.env.REACT_APP_DEV_URL + item?.attributes?.img?.data[0]?.attributes?.url} alt="" />
                    </div>
                    <div className="prod-details">
                        <span className="name">{item?.attributes?.title}</span>
                        <MdClose className="close-btn" onClick={() => handleRemoveFromCart(item)} />
                        <div className="quantity-buttons">
                                <span onClick={() => handleCartProductQuantity('decrement', item)}>-</span>
                                <span>{item?.attributes?.quantity}</span>
                                <span  onClick={() => handleCartProductQuantity('increment', item)}>+</span>
                        </div>
                        <div className="text">
                            <span>3</span>
                            <span>x</span>
                            <span className="highlight">${item?.attributes?.price}</span>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default CartItem;
