import { MdClose } from "react-icons/md";
import { AiOutlineSearch } from "react-icons/ai"
import prod from "../../../assets/products/watch-prod-1.webp";
import { useNavigate } from "react-router-dom";

import "./Search.scss";
import { useState } from "react";
import useFetch from "../../../hooks/useFetch";

const Search = ({ setShowSearch }) => {
    const [query, setQuery] = useState("");
    const navigate = useNavigate();

    const onChange = (e) => {
        setQuery(e.target.value);
    }

    let { data } = useFetch(`/api/products?populate=*&filters[title][$contains]=${query}`);

    if(!query.length) {
        data = null;
    }
    return (
        <div className="search-modal">  
            <div className="form-field">
                <div className="wrap-input">
                    <AiOutlineSearch />
                    <input 
                        type="text"
                        autoFocus
                        placeholder="Search for prosducts"
                        value={query}
                        onChange={onChange}
                    />
                    <MdClose onClick={() => setShowSearch(false)} />
                </div >

                <div className="search-result-content">
                    <div className="search-results">
                            {
                                data?.data?.map((item) => (
                                    <div key={item.id} className="search-result-item" onClick={() => navigate('/product/' + item.id)}>
                                        <div className="image-container">
                                            <img src={process.env.REACT_APP_DEV_URL + item?.attributes?.img?.data[0]?.attributes?.url} alt="" />
                                        </div>
                                        <div className="prod-details">
                                            <span className="name">{item?.attributes?.title}</span>
                                            <span className="decs">{item?.attributes?.desc}</span>
                                        </div>
                                    </div>
                                ))
                            }

                    </div>
                </div>
            </div>
        </div>
    );
};

export default Search;
